CREATE DEFINER=`root`@`localhost` PROCEDURE `BatchUpdateAccount`(IN user_data JSON)
BEGIN
	-- 声明变量
    DECLARE user_count INT;
    DECLARE i INT DEFAULT 0;

    -- 获取用户数量
    SET user_count = JSON_LENGTH(user_data);

    -- 循环更新用户数据
    WHILE i < user_count DO
        -- 获取用户信息
        SET @user_info = JSON_UNQUOTE(JSON_EXTRACT(user_data, CONCAT('$[', i, ']')));

        -- 更新用户数据
        UPDATE newuseraccounts
        SET
         	is_vip = JSON_UNQUOTE(JSON_EXTRACT(@user_info, '$.is_vip')),
         	housecard = JSON_UNQUOTE(JSON_EXTRACT(@user_info, '$.housecard')),
            vip_score = JSON_UNQUOTE(JSON_EXTRACT(@user_info, '$.vip_score')),
            firstRecharge = JSON_UNQUOTE(JSON_EXTRACT(@user_info, '$.firstRecharge')),
            bankLock = JSON_UNQUOTE(JSON_EXTRACT(@user_info, '$.bankLock'))
        WHERE
            id = JSON_UNQUOTE(JSON_EXTRACT(@user_info, '$.id'));
           
           
         -- 更新账户数据
        UPDATE userinfo_imp
        SET
         	score = JSON_UNQUOTE(JSON_EXTRACT(@user_info, '$.score')),
         	diamond = JSON_UNQUOTE(JSON_EXTRACT(@user_info, '$.diamond')),
            bankScore = JSON_UNQUOTE(JSON_EXTRACT(@user_info, '$.bankScore')),
            luckyCoin = JSON_UNQUOTE(JSON_EXTRACT(@user_info, '$.luckyCoin'))
        WHERE
            userId = JSON_UNQUOTE(JSON_EXTRACT(@user_info, '$.id'));

        SET i = i + 1;
    END WHILE;
   
 
END ;;
